sys_script_include.script:
var GetBannedInfo = Class.create();
GetBannedInfo.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getBanned: function() {

		var destID = this.getParameter('sysparm_dest');
		gs.info('KMH - ' + destID);
		var banned = '';


		var nations = new GlideRecord('x_684922_int_trav_nations');
		nations.addQuery('name', destID);
		gs.addInfoMessage(nations);
		nations.query();

		if (nations.next()) {
			banned = nations.banned;
			gs.info('KMH - ' + banned.getBooleanValue);
		}

		return banned.getBooleanValue();
	},

    type: 'GetBannedInfo'
});
